xwikidoc.content:
{{velocity}}
#set ($query = '
{
     "query": {
        "query_string": {
           "query": "-distributionVersion:*SNAPSHOT AND distributionId:\"org.xwiki.enterprise:xwiki-enterprise-web\""
        }
     },
    "aggs": {
        "activeinstalls_over_time" : {
            "date_histogram" : {
                "field" : "_timestamp",
                "interval" : "day"
            },
            "aggs" : {
                "instanceId_count" : {
                    "cardinality" : {
                        "field" : "instanceId"
                    }
                }
            }
        }
    }
}
')
#set ($result = $services.activeinstalls.searchInstalls("install2", $query, {"search_type": "count"}))
#set ($headingLine = "|=Date|=Active Installs Count")
#if (!$result)
  #set ($errorText = $!services.activeinstalls.lastError)
  {{error}}{{{$errorText}}}{{/error}}

  ## Still generate an empty table so that the chart macro using it doesn't fail!
  $headingLine
#else
  ## Generate table with data
  #set ($jsonItems = $result.getAsJsonObject('aggregations').getAsJsonObject('activeinstalls_over_time').getAsJsonArray('buckets'))
  $headingLine
  #foreach ($jsonItem in $jsonItems)
    #set ($date = $datetool.toDate($jsonItem.getAsJsonPrimitive('key').getAsLong()))
    |$datetool.format('yyyy-MM-dd', $date)|$jsonItem.getAsJsonObject('instanceId_count').getAsJsonPrimitive('value').getAsLong()
  #end
#end
{{/velocity}}
