property.code:
/**
 * Add a dependency to the "bootstrap-select" plugin, used to display a nice select field that handle
 * multiple selection.
 */
require.config({
  paths: {
    'bootstrap-select': '$services.webjars.url("bootstrap-select", "js/bootstrap-select.min")'
  },
  shim: {
    'bootstrap-select' : ['jquery', 'bootstrap']
  }
});
/**
 * Replace the simple text input field for supported locales by a nicer select widget with all available locales
 * ready to be selected.
 *
 * Instead of dynamically replace the text input field on the client-side, we could have done on the server-side,
 * by using a custom displayer.
 * However, it's a pain to upgrade the XWiki.XWikiPreferences class to add a displayer.
 * The class is not even packaged by the administration application.
 *
 * We also enable the improved select widget on the "timezone" field to make the user able to filter the choices.
 */
require(['jquery','bootstrap','bootstrap-select'], function($) {
  'use strict';

  var locales = [
#set ($separator = '')
#set ($currentLocale = $services.localization.currentLocale)
#set ($locales = $services.localization.availableLocales)
#foreach ($locale in $sorttool.sort($locales, 'displayName'))
  #if ("$!locale" != '')
    $separator
    { code: '$locale', name: '$escapetool.xml($stringtool.capitalize($locale.getDisplayName($locale))) <small class="text-muted">($stringtool.capitalize($locale.getDisplayName($currentLocale)))</small>'}##
    #set ($separator = ',')
  #end
#end
  ];

  /**
   * Get the initial values of the input
   * @return an array of locales codes
   */
  var getLocalesFromInput = function (input) {
    var locales = input.val().split(/,| /);
    var results = [];
    for (var i = 0; i < locales.length; ++i) {
      if (locales[i].trim() != '') {
        results[results.length] = locales[i].trim();
      }
    }
    return results;
  }

  /**
   * Serialize an array of locales to a string that the XWiki server can handle
   */
  var serializeArray = function (array) {
    // bootstrap-select returns null when nothing is selected.
    array = array || [];
    var result = '';
    var separator = '';
    for (var i = 0; i < array.length; ++i) {
      if (array[i] != '') {
        result += separator;
        result += array[i];
        separator = ',';
      }
    }
    return result;
  }

  /**
   * Initialization
   */
  $(document).ready(function() {

    /**
     * Enable the bootstrap-select plugin to have a nicer UI.
     */
    var enableSelectPicker = function (select) {
      // Force 100% width on the select using the form-control CSS class.
      select.addClass('form-control').selectpicker({
        // Enable the filtering on choices if there is a lot of choices
        liveSearch: select.find('option').length > 10
      });
    }

    //
    // Adding the CSS of the boostrap-select plugin
    //
    var css = $('<link rel="stylesheet" href="$services.webjars.url("bootstrap-select", "css/bootstrap-select.min.css")">');
    $('head').append(css);

    // Getting the original input
    var localesInput = $('input[name="XWiki.XWikiPreferences_0_languages"]');

    // Creating the select box
    var select = $('<select multiple>');

    // Add all available options
    var currentLocales = getLocalesFromInput(localesInput);
    for (var i = 0; i < locales.length; ++i) {
      var option = $('<option>');
      option.prop('value', locales[i].code);
      option.html(locales[i].name);
      if ($.inArray(locales[i].code, currentLocales) > -1) {
        option.prop('selected', 'selected');
      }
      select.append(option);
    }

    // Problem: when we save the document, XWiki expects 'XWiki.XWikiPreferences_0_languages' to be a simple text,
    // not a "select" field with different values.
    // So we keep the old input field as an hidden input which will hold the serialized representation of the
    // selected locales.
    localesInput.prop('type', 'hidden');

    // Adds the new select widget in the DOM
    select.insertAfter(localesInput);

    // Enable the select picker
    enableSelectPicker(select);

    // Synchronize the value of the hidden input text with the selected item in the select widget.
    select.on('changed.bs.select', function (event) {
      localesInput.prop('value', serializeArray(select.val()));
    });

    // We can also do it for the "default locale" field.
    var defaultLocaleInput = $('input[name="XWiki.XWikiPreferences_0_default_language"]');
    var select2 = $('<select>');
    select2.prop('name', defaultLocaleInput.prop('name'));
    select2.prop('id', defaultLocaleInput.prop('id'));
    for (var i = 0; i < locales.length; ++i) {
      var option = $('<option>');
      option.prop('value', locales[i].code);
      option.html(locales[i].name);
      if (defaultLocaleInput.val() == locales[i].code) {
        option.prop('selected', 'selected');
      }
      select2.append(option);
    }
    defaultLocaleInput.replaceWith(select2);

    // Enable bootstrap-select on the "timezone" field
    enableSelectPicker($('select[name="XWiki.XWikiPreferences_0_timezone"]'));

    // Enable it on the default locale field too
    enableSelectPicker($('select[name="XWiki.XWikiPreferences_0_default_language"]'));

    // Enable it on the last select field to be consistent
    enableSelectPicker($('select[name="XWiki.XWikiPreferences_0_multilingual"]'));
  });
});
