Event.observe(document, 'xwiki:dom:loaded', function() {
  var onFocus = function() {
    this.removeClassName('blurred');
  }
  var onBlur = function() {
    if (this.value.length == 0 || this.value == this.defaultValue) {
      this.addClassName('blurred');
    }
  }
  $$('input.withTip').each(function(item) {
    item.observe('focus', onFocus.bind(item));
    item.observe('blur', onBlur.bind(item));
    if (item.value == item.defaultValue) {
      item.addClassName('blurred');
    }
  });
});

