) #hideMessageBox($services.localization.render('xe.blog.sheet.hidearticle', [${hideArticle}])) #end #else #set($defaultDate = $xwiki.getDocument($blogPostTemplate).getObject($blogPostClassname).getProperty('publishDate').value.time) #if($entryObj.getProperty('publishDate').value.time == $defaultDate) ## The publish date was not set, force it to be the creation date $entryObj.set('publishDate', $doc.creationDate) #end {{html clean="false" wiki="true"}} #publishMessageBox("$services.localization.render('xe.blog.sheet.notpublished') <label>**$services.localization.render('xe.blog.sheet.publish') ${doc.display('published', 'edit', $entryObj)}**</label>\\<label>$services.localization.render('xe.blog.sheet.setdate') ${doc.display('publishDate', 'edit', $entryObj)}</label>") {{/html}} #end #end {{/velocity}}
