property.code:
/* Administration application custom JavaScript */

document.observe('xwiki:dom:loaded', function() {
  if($('goto-select')) {
    Event.observe($('goto-select'), 'change', function (event) {
      var select = event.element();
      var i = select.selectedIndex;
      if (window.location != select.options[i].value) {
        window.location = select.options[i].value;
      }
    });
    $('goto-select').next().hide();
  }
});

#document-title {
  margin-bottom: 1em;
}
#change-context {
  float: right;
  padding-top: 0.5em;
}
#goto-select optgroup {
  margin: 0.5ex;
}

#syntax-page-content {
  margin-left: 15em;
}
#syntax-page-content table {
  width: 99%;
}
#syntax-page-content>h3 {
  display: none;
}
#syntax-page-content>h3.sectionheader {
  display: block;
}

