property.code:
(function(){

if (typeof XWiki == "undefined") {
   XWiki = new Object();
}

if (typeof XWiki.index == "undefined") {
   XWiki.index = new Object();
}

var mimetypes = {
  "image/svg"                     : { "src" : "$xwiki.getSkinFile('icons/silk/vector.png')", "alt" : "${msg.get('core.viewers.attachments.mime.svg')}", "title" : "${msg.get('core.viewers.attachments.mime.svg')}" },
  "application/svg+xml"           : { "src" : "$xwiki.getSkinFile('icons/silk/vector.png')", "alt" : "${msg.get('core.viewers.attachments.mime.svg')}", "title" : "${msg.get('core.viewers.attachments.mime.svg')}" },
  "text/plain"                    : { "src" : "$xwiki.getSkinFile('icons/silk/page_white_text.png')", "alt" : "${msg.get('core.viewers.attachments.mime.text')}", "title" : "${msg.get('core.viewers.attachments.mime.text')}"},
  "text/html"                     : { "src" : "$xwiki.getSkinFile('icons/silk/html.png')", "alt" : "${msg.get('core.viewers.attachments.mime.html')}", "title" : "${msg.get('core.viewers.attachments.mime.html')}"},
  "text/css"                      : { "src" : "$xwiki.getSkinFile('icons/silk/css.png')", "alt" : "${msg.get('core.viewers.attachments.mime.css')}", "title" : "${msg.get('core.viewers.attachments.mime.css')}" },
  "application/xml"               : { "src" : "$xwiki.getSkinFile('icons/silk/page_white_code.png')", "alt" : "${msg.get('core.viewers.attachments.mime.xml')}", "title" : "${msg.get('core.viewers.attachments.mime.xml')}" },
  "application/pdf"               : { "src" : "$xwiki.getSkinFile('icons/silk/page_white_acrobat.png')", "alt" : "${msg.get('core.viewers.attachments.mime.pdf')}", "title" : "${msg.get('core.viewers.attachments.mime.pdf')}" },
  "application/postscript"        : { "src" : "$xwiki.getSkinFile('icons/silk/page_red.png')", "alt" : "${msg.get('core.viewers.attachments.mime.ps')}",   "title" : "${msg.get('core.viewers.attachments.mime.ps')}"  },
  "application/msword"            : { "src" : "$xwiki.getSkinFile('icons/silk/page_white_word.png')", "alt" : "${msg.get('core.viewers.attachments.mime.document')}", "title" : "${msg.get('core.viewers.attachments.mime.document')}"},
  "application/powerpoint"        : { "src" : "$xwiki.getSkinFile('icons/silk/page_white_powerpoint.png')", "alt" : "${msg.get('core.viewers.attachments.mime.presentation')}", "title" : "${msg.get('core.viewers.attachments.mime.presentation')}" },
  "application/x-shockwave-flash" : { "src" : "$xwiki.getSkinFile('icons/silk/page_white_flash.png')", "alt" : "${msg.get('core.viewers.attachments.mime.flash')}", "title" : "${msg.get('core.viewers.attachments.mime.flash')}" },
  "application/x-tar"             : { "src" : "$xwiki.getSkinFile('icons/silk/page_white_compressed.png')", "alt" : "${msg.get('core.viewers.attachments.mime.tar')}", "title" : "${msg.get('core.viewers.attachments.mime.tar')}" },
  "application/zip"               : { "src" : "$xwiki.getSkinFile('icons/silk/page_white_zip.png')", "alt" : "${msg.get('core.viewers.attachments.mime.zip')}", "title" : "${msg.get('core.viewers.attachments.mime.zip')}" },
  "application/x-gzip"            : { "src" : "$xwiki.getSkinFile('icons/silk/page_white_compressed.png')", "alt" : "${msg.get('core.viewers.attachments.mime.gz')}", "title" : "${msg.get('core.viewers.attachments.mime.gz')}"  },
  "application/java-archive"      : { "src" : "$xwiki.getSkinFile('icons/silk/cup.png')", "alt" : "${msg.get('core.viewers.attachments.mime.jar')}", "title" : "${msg.get('core.viewers.attachments.mime.jar')}" }
};

/**
 * JavasScript equivalent of the #mimetype macro.
 * Returns a 20px * 20px image (as an element) corresponding to the passed mimetype.
 * First lookup if the type is in the mimetypes object above, then handles some other cases (starts/end with, etc.)
 * This function should be cleaned at the same time the mimetype images and macro are.
 */
function getMimeTypeImage(type) {
  var image = undefined;
  if (mimetypes[type]) {
    image = new Element('img',{'src':mimetypes[type].src,'alt':mimetypes[type].alt,'title':mimetypes[type].title});
  }
  else if(type.match(/^image\//)) {
    image = new Element('img', {'src':"$xwiki.getSkinFile('icons/silk/picture.png')", 'alt':"${msg.get('core.viewers.attachments.mime.image')}", 'title': "${msg.get('core.viewers.attachments.mime.image')}"});
  }
  else if(type.match(/^text\//)) {
    image = new Element('img', {'src':"$xwiki.getSkinFile('icons/silk/page_white_text.png')", 'alt':"${msg.get('core.viewers.attachments.mime.text')}", 'title': "${msg.get('core.viewers.attachments.mime.text')}"});
  }
  else if(type.match(/^audio\//)){
    image = new Element('img', {'src':"$xwiki.getSkinFile('icons/silk/music.png')", 'alt':"${msg.get('core.viewers.attachments.mime.audio')}", 'title': "${msg.get('core.viewers.attachments.mime.audio')}"});
  }
  else if(type.match(/^video\//)){
    image = new Element('img', {'src':"$xwiki.getSkinFile('icons/silk/film.png')", 'alt':"${msg.get('core.viewers.attachments.mime.video')}", 'title': "${msg.get('core.viewers.attachments.mime.video')}"});
  }
  else if(type=="application/octet-stream") {
    if (type.match(/\.xls$/)) {
      image = new Element('img', {'src':"$xwiki.getSkinFile('icons/silk/page_white_excel.png')", 'alt':"${msg.get('core.viewers.attachments.mime.spreadsheet')}", 'title': "${msg.get('core.viewers.attachments.mime.spreadsheet')}"});
    }
    else if (type.match(/\.bz$/)) {
      image = new Element('img', {'src':"$xwiki.getSkinFile('icons/silk/page_white_compressed.png')", 'alt':"${msg.get('core.viewers.attachments.mime.bz')}", 'title': "${msg.get('core.viewers.attachments.mime.bz')}"});
    }
    else if (type.match(/\.tgz$/)) {
      image = new Element('img', {'src':"$xwiki.getSkinFile('icons/silk/page_white_compressed.png')", 'alt':"${msg.get('core.viewers.attachments.mime.gz')}", 'title': "${msg.get('core.viewers.attachments.mime.gz')}"});
    }
    else if (type.match(/\.rar$/)) {
      image = new Element('img', {'src':"$xwiki.getSkinFile('icons/silk/page_white_compressed.png')", 'alt':"${msg.get('core.viewers.attachments.mime.rar')}", 'title': "${msg.get('core.viewers.attachments.mime.rar')}"});
    }
    else if (type.match(/\.(odt|odp|odf|ods|sxw|stw)$/)) {
      image = new Element('img', {'src':"$xwiki.getSkinFile('icons/silk/page.png')", 'alt':"${msg.get('core.viewers.attachments.mime.office')}", 'title': "${msg.get('core.viewers.attachments.mime.office')}"});
    }
  } 
  else if (type.match(/^application\//)) {
    image = new Element('img', {'src':"$xwiki.getSkinFile('icons/silk/cog.png')", 'alt':"${msg.get('core.viewers.attachments.mime.exe')}", 'title': "${msg.get('core.viewers.attachments.mime.exe')}"});
  }
  if (typeof image == "undefined") {
    image = new Element('img', {'src': "$xwiki.getSkinFile('icons/silk/page_white.png')", 'alt':"${msg.get('core.viewers.attachments.mime.attachment')}", 'title': "${msg.get('core.viewers.attachments.mime.attachment')}"});
  }
  image.style.width = '16px';
  image.style.height = '16px';
  return image;
}

XWiki.index.displayAttachmentEntry = function (row, i, table) {
  var inaccessibleDocs = false;
  if(row.acclev == true) {
    var fileLink = new Element('a', {'href' : row.fileurl}).update(row.filename);
    var tr = new Element('tr').update(new Element('td').update(fileLink));
    var pageLink = new Element('a', {'href' : row.url}).update(row.page);
    tr.appendChild(new Element('td', {'class' : 'pagename'}).update(pageLink));
    var spaceLink = new Element('a', {'href' : row.spaceurl}).update(row.space);
    tr.appendChild(new Element('td', {'class' : 'spacename'}).update(spaceLink));
    tr.appendChild(new Element('td').update(row.date));
    var aa = new Element('a', {'href' : row.authorurl}).update(row.authorname);
    tr.appendChild(new Element('td').update(aa));
    var type = new Element('td').update(getMimeTypeImage(row.type));
    tr.appendChild(type);
    return tr;
  } else {
    var tr = new Element('tr');
    var page = row.fullname;

    var td1 = new Element('td').update("unavailable");
    var td2 = new Element('td', {'class' : 'pagename'}).update(page + "*");
    var td3 = new Element('td').update(" ");
    var td4 = new Element('td').update(" ");
    var td5 = new Element('td').update(" ");
    var td6 = new Element('td').update(" ");
    tr.appendChild(td1);
    tr.appendChild(td2);
    tr.appendChild(td3);
    tr.appendChild(td4);
    tr.appendChild(td5);
    tr.appendChild(td6);
    $('inaccessibleDocsMessage').removeClassName('hidden');
    return tr;
  }
}

})();
