require(['jquery'], function ($) {
  var form = $('#realtime\\ editors_XWiki\\.Realtime\\.Configuration');
  if (XWiki.mainWiki === XWiki.currentWiki) {
    form.find('dt:first').hide();
    return;
  }
  var onChange = function () {
    var useGlobal = $(this).prop('checked');
    form.find('input:visible, textarea:visible, select:visible').prop('disabled', useGlobal);
    $(this).prop('disabled', false);
  };
  $('#XWiki\\.Realtime\\.Configuration_XWiki\\.Realtime\\.ConfigurationClass_0_useGlobalConfig').change(onChange)
    .trigger('change');
});
