property.code:
require(['jquery', 'xwiki-meta'], function ($, xm) {
  const SHORTCUT_XOBJ_ACTION = Object.freeze({
    CREATE: 'create',
    DELETE: 'delete',
    UPDATE: 'update',
    NONE: 'none'
  });
  /**
   * Handle the shortcut preference modifications
   */
  $(document).off('input.shortcutPreference');
  let updateShortcut = function (shortcutPreferenceInput) {
    // Step 1: We retrieve information about the shortcut preference state.
    let shortcutPreference = $(shortcutPreferenceInput);
    let translateKey = shortcutPreference.attr("data-translate-key");
    let newValue = shortcutPreference.val();
    let oldValue = shortcutPreference.attr('value');
    let defaultValue = shortcutPreference.attr("data-default-value");
    let url = xm.restURL;
    let oldItemNumber = shortcutPreference.attr("data-shortcut-pref-number");
    // Step 2: We want to compute what is the XObject action we'll need to take when the form is submitted
    let xObjectAction = SHORTCUT_XOBJ_ACTION.NONE;
    // If the new value is the same as the one that is already here, we do not have to do any action on the XObject
    if (newValue === oldValue) { xObjectAction = SHORTCUT_XOBJ_ACTION.NONE; }
    else {
      // If it's equal to the default value from the translation 
      // (the translation is different to the default value so there was a preference)
      // Then we can remove the preference object. There's no need to store user preferences about this.
      if (newValue === defaultValue) { xObjectAction = SHORTCUT_XOBJ_ACTION.DELETE; }
      else {
        // The new value is different from both the old value and the translation value (default)
        // If the old value is the same as the default value then it means there was no object, 
        // and we should create one.
        // If not, it means that the shortcut was already customized and we should just update the object.
        if (defaultValue === oldValue) { xObjectAction = SHORTCUT_XOBJ_ACTION.CREATE; }
        else { xObjectAction = SHORTCUT_XOBJ_ACTION.UPDATE; }
      }
    }
    // Step 3: We retrieve information about the current state of the form
    // All the hidden inputs are stored as siblings of the main input.
    let inputCreation = shortcutPreference.siblings('input[name=addedObjects]');
    let inputDeletion = shortcutPreference.siblings('input[name=deletedObjects]');
    let inputUpdate = shortcutPreference.siblings('input[name=addedObjects]');
    // Step 4: We update the form so that it properly updates the state of the XObjects when submitted.
    if (oldItemNumber == null) {
      if (newValue !== defaultValue) {
        // Create the new user preference
        let creationUrl = url + "/objects";
        let data = {
          'className': 'XWiki.UserPreferenceShortcutsClass',
          'property#translationKey': translateKey,
          'property#value': newValue
        };
        $.post(creationUrl, data, function(newObject) {
          shortcutPreference.attr("data-shortcut-pref-number", newObject.firstChild.getElementsByTagName("number")[0].textContent);
        });
      }
    } else {
      if (newValue == shortcutPreference.attr("data-default-value")) {
        // Remove the existing preference, we don't want to keep it if the user is back to normal.
        let destructionUrl = url + "/objects/XWiki.UserPreferenceShortcutsClass/" + oldItemNumber;
        $.ajax({url: destructionUrl,
          type: "DELETE"
        });
        shortcutPreference.removeAttr("data-shortcut-pref-number");
      } else {
        // Update the existing preference
        let updateUrl = url + "/objects/XWiki.UserPreferenceShortcutsClass/" + oldItemNumber +"/properties/value";
        let data = {
          'property#value': newValue
        };
        $.ajax({url: updateUrl,
          type: "PUT",
          data: data
        });
      }
    }
  };
  /**
   * Handle the reset buttons for shortcut preferences.
   */
  $('.reset-shortcut-preference').on('click', function (event) {
    const shortcutPrefInput = $(this).siblings('input');
    const defaultValue = shortcutPrefInput.attr('data-default-value');
    shortcutPrefInput.val(defaultValue).trigger("input");
  });

  // Buffer system to avoid updating the state of the preferences at each small change.
  var timeoutId = 0;
  var lastInput = null;
  $(document).on('input.shortcutPreference', '.shortcutPreference', function () {
    // If we propose a new update for the same field quickly after it was already updated, 
    // we cancel the previous update and start the timer for this new one.
    if (timeoutId !== 0 && $(this).get(0).id == lastInput) {
      clearTimeout(timeoutId);
    }
    lastInput = $(this).get(0).id;
    timeoutId = setTimeout(updateShortcut, 500, this);
  });
});
