  background: url("$xwiki.getSkinFile('icons/silk/world.png')") no-repeat;
}

span.spaceWatchlistType {
  background: url("$xwiki.getSkinFile('icons/silk/sitemap.png')") no-repeat;
}

span.documentWatchlistType {
  background: url("$xwiki.getSkinFile('icons/silk/page_white_text.png')") no-repeat;
}

{{velocity html="true"}}
#if (!$isGuest)
$xwiki.ssx.use("XWiki.WatchListMacro")
{{html wiki="true"}}
<div class="watchlistContainer">
  #set($collist = ['type', 'location', 'scope', 'actions'])
  #set($colprops = { 
      "type": { "sortable":false },
      "location": { "sortable":false },
      "scope": { "sortable":false },
      "actions": { "sortable":false }
  })
  #set($options = { "resultPage" : "XWiki.XWikiUserWatchListLiveTableResults",
      "rowCount": 10,
      "description": "This table lists all watched elements.",
      "translationPrefix": "watchlist.table.",
      "callback": "function (row, i, table) {
                      var tr = new Element('tr');
                      var typeIcon = '';
                      var scope = '';
                      if (row.type == 'document') {
                        typeIcon = '$escapetool.javascript($services.icon.renderHTML('page'))';
                        scope = '$escapetool.javascript($escapetool.xml($services.localization.render('watchlist.table.scope.pageonly')))';
                      } else if (row.type == 'space') {
                        typeIcon = '$escapetool.javascript($services.icon.renderHTML('chart-organisation'))';
                        scope = '$escapetool.javascript($escapetool.xml($services.localization.render('watchlist.table.scope.pageandchildren')))';  
                      } else if (row.type == 'wiki') {
                        typeIcon = '$escapetool.javascript($services.icon.renderHTML('wiki'))';
                        scope = '$escapetool.javascript($escapetool.xml($services.localization.render('watchlist.table.scope.wiki')))';
                      }
                      tr.insert(new Element('td', {'data-title':'$escapetool.javascript($escapetool.xml($services.localization.render('watchlist.table.type')))'}).update(typeIcon));
                      tr.insert(new Element('td', {'data-title':'$escapetool.javascript($escapetool.xml($services.localization.render('watchlist.table.location')))'}).update(row.location));
                      tr.insert(new Element('td', {'data-title':'$escapetool.javascript($escapetool.xml($services.localization.render('watchlist.table.scope')))'}).update(scope));
                      var deleteLink = new Element('a', {
                         href : row.deleteUrl,
                         title : '$escapetool.javascript($services.localization.render('platform.core.profile.watchlist.unwatch'))'
                      });
                      deleteLink.insert(new Element('img', {
                        'src' : '${xwiki.getSkinFile('icons/silk/cross.png')}'
                      }));
                      deleteLink.observe('click', function(event) {
                        Event.stop(event);
                        if (row.deleteUrl.indexOf('/') == 0) {
                          // DeleteURL is from the same domain
                          var surl = row.deleteUrl;
                          var myAjax = new Ajax.Request(
                            surl,
                            {
                              method: 'get',
                              onComplete: function(transport) {
                                this.clearCache();
                                this.showRows(this.currentOffset, this.limit);
                              }.bind(this)
                            }
                          );
                        } else {
                          // DeleteURL is from another domain, which prevents an AJAX request to be used here. We make a browser request and use xredirect to come back to the current page.
                          var separator = (row.deleteUrl.indexOf('?') == -1) ? '?' : '&';
                          window.location = row.deleteUrl + separator + 'xredirect=' + window.location;
                        }
                      }.bind(this));
                      tr.insert(new Element('td', {'data-title':'$escapetool.javascript($escapetool.xml($services.localization.render('watchlist.table.actions')))'}).insert(deleteLink));
                      return tr;
                   }" })

  #set($watchlistCtr = $request.getAttribute('XWiki.WatchListMacro:counter'))
  #if(!$watchlistCtr) #set($watchlistCtr = 0) #else #set($watchlistCtr = $watchlistCtr + 1) #end
  #set($discard = $request.setAttribute('XWiki.WatchListMacro:counter', $watchlistCtr))
  #livetable("mywatchlist-$watchlistCtr" $collist $colprops $options)
</div>
{{/html}}
#end
{{/velocity}}
