(% class="xform" %)(((
  #foreach($prop in $class.properties)
    ; {{html clean="false"}}
      $doc.displayPrettyName($prop.name)
      #if($prop.hint)
        $prop.hint
      #end
    {{/html}}
    : $doc.display($prop.name)
  #end
)))
